import React, { Component } from 'react';
import Calories from './components/Calories'
import Excersize from './components/Excersize'
import Time from './components/Time'
import CalorieList from './components/CalorieList'

import './App.css';

class App extends Component {
  state = {
    amount: [],
  }

  addCalories = (amount) => {
    this.setState({amount : [...this.state.amount, amount]});
  }
  render() {
    return (
      <div className="App">
        <div className="Title">
        <header>
          <h1 style={titleStyle}>Working with React</h1>
        </header>
        </div>
          <div className="Calories" style={cStyle}>
            <CalorieList amount={this.state.amount}/>
            <Calories addCalories={this.addCalories}/>
          </div>
          <div className="Excersize" style={eStyle}>
            <Excersize/>
            <Time/>
          </div>
      </div>
    );
  }
}

const titleStyle = {
  textAlign: 'center',
  backgroundColor: 'grey',
}

const cStyle = {
  textAlign: 'left'
}

const eStyle = {
  textAlign: 'center'
}
export default App;
