import React, { Component } from 'react'

export class Excersize extends Component {
    state = {
        type: '',
        length: 0,

    }

    onChange = (e) => {
      if (e.target.type === "radio"){

        this.setState({type: e.target.value});
        console.log(this.state);
      }
      else{
        this.setState({length : e.target.value})
      }
    }

    onSubmit = (e) => {
      e.preventDefault();
      this.props.addExcersize(this.state);
      this.setState({type: '', length: 0});
    }
  render() {

    const options = [
        0, 5, 10, 15, 20, 25, 30, 45, 60
    ]
    const dropOptions = options.map((x) => {
        return(<option key={x}>{x}</option>)
      });

    return (

      <form onSubmit={this.onSubmit} style={{display : 'flex'}}>
        <div>
            <label className="Type">
            Ran
            <input type="radio" value="Ran" onChange={this.onChange}></input>
            <div></div>
            Biked
            <input type="radio" value="Biked" onChange={this.onChange}></input>
            <div></div>
            Upper body
            <input type="radio" value="Upper" onChange={this.onChange}></input>
            <div></div>
            Lower body
            <input type="radio" value="Lower" onChange={this.onChange}></input>
            <div></div>
            <div>
          <label>Time in minutes</label>
          <select value={this.state.length} onChange={this.onChange}>{dropOptions}</select>
          </div>
        </label>
        </div>
        <input type='submit' value='Submit' className='btn'></input>
      </form>
    )
  }
}

export default Excersize
