import React, { Component } from 'react'

export class Excersize extends Component {
    state = {
        type: '',
        length: 0,
    }

    onChange = (e) => {
      if (e.target.type === "checkbox"){
        this.setState({type: e.target.value});
        console.log(this.state);
      }
      else{
        this.setState({length : e.target.value})
      }
    }
  render() {
    const options = [
        0, 5, 10, 15, 20, 25, 30, 45, 60
    ]
    const dropOptions = options.map((x) => {
        return(<option key={x}>{x}</option>)
      });

    return (
      <div>
        <label className="Type">
            Ran
            <input type="checkbox" value="Ran" onChange={this.onChange}></input>
            <div></div>
            Biked
            <input type="checkbox" value="Biked" onChange={this.onChange}></input>
            <div></div>
            Upper body
            <input type="checkbox" value="Upper" onChange={this.onChange}></input>
            <div></div>
            Lower body
            <input type="checkbox" value="Lower" onChange={this.onChange}></input>
            <div></div>
            <div>
          <label>Time in minutes</label>
          <select value={this.state.length} onChange={this.onChange}>{dropOptions}</select>
      </div>
        </label>
      </div>
    )
  }
}

export default Excersize
